mapper.select:
select 
from message
where from_id = #{userId}
or to_id = #{userId}
order by create_time asc
limit #{offset}, #{limit}

select count(*)
from message
where from_id = #{userId}
or to_id = #{userId}

select 
from message
where conversation_id = #{conversationId}
order by create_time asc
limit #{offset}, #{limit}

select count(*)
from message
where conversation_id = #{conversationId}

select count(*)
from message

where conversation_id = #{conversationId}
